import { NestFactory } from '@nestjs/core';
import { AppModule } from '../src/app.module';
import { ValidationPipe } from '@nestjs/common';
import { SwaggerModule, DocumentBuilder } from '@nestjs/swagger';

let app: any;

async function createApp() {
  if (!app) {
    app = await NestFactory.create(AppModule, {
      logger: ['error', 'warn'],
    });

    app.useGlobalPipes(
      new ValidationPipe({
        whitelist: true,
        forbidNonWhitelisted: true,
        transform: true,
      }),
    );

    app.enableCors({
      origin: process.env.NEXT_PUBLIC_APP_URL || '*',
      credentials: true,
    });

    // Setup Swagger only in development
    if (process.env.NODE_ENV !== 'production') {
      const config = new DocumentBuilder()
        .setTitle('ERP API')
        .setDescription('ERP System API Documentation')
        .setVersion('1.0')
        .addBearerAuth()
        .build();
      const document = SwaggerModule.createDocument(app, config);
      SwaggerModule.setup('api/docs', app, document);
    }

    await app.init();
  }
  return app;
}

export default async function handler(req: any, res: any) {
  const app = await createApp();
  const expressApp = app.getHttpAdapter().getInstance();

  // Remove the /api/server prefix from the URL for NestJS routing
  req.url = req.url.replace(/^\/api\/server/, '') || '/';

  return expressApp(req, res);
}

export const envConfig = {
  appUrl: process.env.NEXT_PUBLIC_APP_URL || "http://localhost:3000",
  apiUrl: process.env.NEXT_PUBLIC_API_URL || "http://localhost:3001",
  nextAuthSecret: process.env.NEXTAUTH_SECRET || "my-secret",
  nextAuthUrl: process.env.NEXTAUTH_URL || "http://localhost:3000",
};

export const envConfig = {
  appUrl: process.env.NEXT_PUBLIC_APP_URL || "http://localhost:3000",
  apiUrl:
    process.env.NODE_ENV === "production"
      ? `${process.env.NEXT_PUBLIC_APP_URL}/api/server`
      : process.env.NEXT_PUBLIC_API_URL || "http://localhost:3001",
  nextAuthSecret: process.env.NEXTAUTH_SECRET || "my-secret",
  nextAuthUrl: process.env.NEXTAUTH_URL || "http://localhost:3000",
};

